def affirmative? question
  # a prompt will be shown to the user
  # we expect a 'y' or 'n' response, case-insensitive
  # if the user types anything else, they'll be asked to re-answer the question
  poss_answers = ['y', 'n']

  puts "#{question} (Y/N)"
  user_answer = gets.chomp.downcase
  unless poss_answers.include? user_answer
    puts "Sorry, I didn't understand that.", ""
    affirmative? question
  else
    user_answer == poss_answers[0]
  end
end

